var bridge = global.get("cloud.bridges").find(bridge => {
    return bridge.id === msg.payload.find(flow => flow.id === msg.thisflow).label;
})
bridge.image.metrics = {};
bridge.error = false;

flow.set("bridge", bridge);

return msg;

msg.payload = "";

msg.sequences = flow.get("bridge.image.protocols").filter(protocol => protocol.type !== "virtual").map(dataPoint => {
    return {
        dataPoint: dataPoint,
        name: dataPoint.id,
        fc: dataPoint.fc,
        address: dataPoint.address,
        unitid: flow.get("bridge.image.controller.unitId"),
        quantity: global.get("function.typeToRegisterLength")(dataPoint.type)
    };
});

if (msg.sequences.length > 0) {
    node.send(msg);
}

node.done();

if (!msg.dataPoint.type.includes("bool") || (msg.dataPoint.type.includes("bool") && typeof msg.payload[0] !== "boolean")) {
    let byteSwap = false;
    if (msg.dataPoint.byteswap !== undefined && msg.dataPoint.byteswap !== null){
        byteSwap = msg.dataPoint.byteswap;
    } else if (msg.dataPoint.type.includes("bool")){
        byteSwap = true;
    }

    msg.spec = {
        "options":
        {
            "byteSwap": byteSwap,
            "resultType": "keyvalue",
            "singleResult": true,
            "setTopic": true,
            "msgProperty": "payload",
        },
        "items": []
    }

    msg.spec.items = [
        {
            "name": msg.name,
            "type": msg.dataPoint.type.includes("bool") ? "bool" : msg.dataPoint.type,
            "length ": global.get("function.typeToRegisterLength")(msg.dataPoint.type),
            "offset": 0,
            "offsetbit": msg.dataPoint.offsetbit,
            "scale": msg.dataPoint.scale.toString(),
            "mask": msg.dataPoint.mask
        }
    ];

    if (msg.dataPoint.type.includes("bool") && typeof msg.payload[0] !== "boolean" && msg.dataPoint.invert) {
        msg.payload[0] = Math.abs(msg.payload[0] - 1)
    }

    node.send(msg);
}

node.done();

if (msg.dataPoint.type.includes("bool") && typeof msg.payload[0] === "boolean") {
    var payload = {};
    payload[msg.name] = msg.payload[msg.dataPoint.offsetbit];

    if (msg.dataPoint.invert) {
        payload[msg.name] = !payload[msg.name];
    }

    msg.payload = payload;
    node.send(msg);
}

node.done();

const controllerParameter = flow.get("bridge.image.controller");
const protocols = flow.get("bridge.image.protocols");
var channel = global.get("hardware.interface.serial.channels").concat(global.get("hardware.interface.tcp.channels")).find(channel => channel.id === controllerParameter.channel);

if (channel && controllerParameter && protocols) {
    if (channel.connectorType == "TCP") {
        msg.payload = {
            "connectorType": controllerParameter.connectorType ? controllerParameter.connectorType : channel.connectorType,
            "tcpHost": controllerParameter.tcpHost,
            "tcpPort": controllerParameter.tcpPort,
            "tcpType": controllerParameter.tcpType ? controllerParameter.tcpType : "DEFAULT",
            "unitId": controllerParameter.unitId ? controllerParameter.unitId : 1,
            "commandDelay": controllerParameter.commandDelay ? controllerParameter.commandDelay : 5,
            "clientTimeout": controllerParameter.clientTimeout ? controllerParameter.clientTimeout : 1000,
            "reconnectTimeout": controllerParameter.reconnectTimeout ? controllerParameter.reconnectTimeout : 2000
        };
    } else {
        msg.payload = {
            "connectorType": controllerParameter.connectorType ? controllerParameter.connectorType : channel.connectorType,
            "serialPort": controllerParameter.serialPort ? controllerParameter.serialPort : channel.serialPort,
            "serialBaudrate": controllerParameter.serialBaudrate ? controllerParameter.serialBaudrate : 19200,
            "serialDatabits": controllerParameter.serialDatabits ? controllerParameter.serialDatabits : 8,
            "serialStopbits": controllerParameter.serialStopbits ? controllerParameter.serialStopbits : 1,
            "serialParity": controllerParameter.serialParity ? controllerParameter.serialParity : "none",
            "serialType": controllerParameter.serialType ? controllerParameter.serialType : "RTU-BUFFERD",
            "serialConnectionDelay": controllerParameter.serialConnectionDelay ? controllerParameter.serialConnectionDelay : 100,
            "unitId": controllerParameter.unitId ? controllerParameter.unitId : 1,
            "commandDelay": controllerParameter.commandDelay ? controllerParameter.commandDelay : 5,
            "clientTimeout": controllerParameter.clientTimeout ? controllerParameter.clientTimeout : 1000,
            "reconnectTimeout": controllerParameter.reconnectTimeout ? controllerParameter.reconnectTimeout : 2000
        };
    }
    node.send(msg);
} else {
    node.warn("Invalid channel configuration!");
}

node.done()

const functions = {
    "deviceState": (metricId, parameterIds) => {
        if (!flow.get("bridge.image.metrics").hasOwnProperty(metricId)) {
            msg.payload[metricId] = 0;
        }
        if (parameterIds.length === 0) {
            parameterIds = flow.get("bridge.image.protocols").filter(dataPoint => {
                return dataPoint.topic.includes("error")
            }).map(dataPoint => {
                return dataPoint.id;
            });
        }

        const ok = Object.keys(flow.get("bridge.image.metrics")).filter(key => {
            return parameterIds.includes(key)
        }).reduce((accumulator, id) => accumulator && (flow.get("bridge.image.metrics")[id] === "" || flow.get("bridge.image.metrics")[id] === 0 || flow.get("bridge.image.metrics")[id] === false), true)

        if (ok) {
            msg.payload[metricId] = 0;
        } else {
            msg.payload[metricId] = 2;
        }
    }
}

const executor = (metricId, func, parameterIds) => {
    msg.payload[metricId] = eval("(" + func + ")")(...parameterIds.map(x => flow.get("bridge.image.metrics")[x]))
}

msg.sequences.forEach(sequence => {
    msg = sequence;
    msg.payload = {};
    let calc = sequence.dataPoint.calculation
    let metricsExist = calc.ids.reduce((accumulator, currentValue) => accumulator && flow.get("bridge.image.metrics").hasOwnProperty(currentValue), true)
    if (metricsExist) {
        if (functions.hasOwnProperty(calc.func)){
            functions[calc.func](sequence.dataPoint.id, calc.ids)
        } else {
            executor(sequence.dataPoint.id, calc.func, calc.ids);
        }
    } else {
        throw "Parameter IDs: " + calc.ids + " is missing for calculation";
    }
    node.send(msg);
});

node.done();

var cloudBridge = global.get("cloud.bridges").find(bridge => {
    return bridge.id === flow.get("bridge").id
})

msg.stateUpstreamRequired = false;
msg.errorUpstreamRequired = false;
for (var propertyName in msg.payload) {
    if (typeof msg.payload[propertyName] === "number") {
        // Round to 2 decimal
        msg.payload[propertyName] = Math.round((msg.payload[propertyName] + Number.EPSILON) * 100) / 100
    }
    if (msg.dataPoint.topic.find(topic => topic === "error") && flow.get("bridge").image.metrics[propertyName] !== undefined && flow.get("bridge").image.metrics[propertyName] !== msg.payload[propertyName]) {
            msg.errorUpstreamRequired = true;
    }
    if (msg.dataPoint.topic.find(topic => topic === "state") && flow.get("bridge").image.metrics[propertyName] !== undefined && flow.get("bridge").image.metrics[propertyName] !== msg.payload[propertyName]) {
        msg.stateUpstreamRequired = true;
    }
    flow.get("bridge").image.metrics[propertyName] = msg.payload[propertyName];
}

cloudBridge.image.metrics = flow.get("bridge").image.metrics;

return msg;

msg.payload = {
    "payload" : msg.payload,
    "error" : msg.error
};

if (msg._error){
    msg.payload._error = msg._error;
}

return msg;

msg.sequences = flow.get("bridge.image.virtuals").map(dataPoint => {
    return {
        dataPoint: dataPoint
    };
});

if (msg.sequences.length > 0) {
    node.send(msg);
}

node.done();

msg.action = "subscribe";
msg.topic = {
    "qos": 2,
    "topic": "bridge/command/" + flow.get("bridge").id + "/#"
}

return msg;

const command = msg.payload.command;
const response = msg.payload.response;
const matchingCommand = flow.get("bridge.image.commands").find(bridgeCommand => bridgeCommand.id === command.id);
const matchingProtocol = matchingCommand ? flow.get("bridge.image.protocols").find(protocol => protocol.id === matchingCommand.protocolId) : undefined;

// FC 15/16 => value muss Array sein
function ensureArrayForMultipleFC(value, fc) {
    if (fc === 15 || fc === 16) {
        return Array.isArray(value) ? value : [value];
    }
    return value;
}

// 16-bit types -> single Modbus register (number 0..65535)
function encode16ToRegister(value, type) {
    const buf = Buffer.alloc(2);

    switch (type) {
        case 'int':
            buf.writeInt16BE(value);
            break;
        case 'int16be':
            buf.writeInt16BE(value);
            break;
        case 'int16le':
            buf.writeInt16LE(value);
            break;
        case 'uint':
            buf.writeUInt16BE(value);
            break;
        case 'uint16be':
            buf.writeUInt16BE(value);
            break;
        case 'uint16le':
            buf.writeUInt16LE(value);
            break;
        case 'int8':
            // store in low byte of the register
            buf.writeInt8(value, 1);
            break;
        case 'uint8':
        case 'byte':
            buf.writeUInt8(value, 1);
            break;
        default:
            throw new Error("Unsupported 16-bit type: " + type);
    }

    // we send Modbus register as an unsigned 16-bit word 0..65535
    return buf.readUInt16BE(0);
}

// 32-Bit Wert in zwei 16-Bit Register umwandeln
function encode32ToRegisters(value, type) {
    const buf = Buffer.alloc(4);

    switch (type) {
        case "int32le":
            buf.writeInt32LE(value);
            break;
        case "int32be":
            buf.writeInt32BE(value);
            break;
        case "uint32le":
            buf.writeUInt32LE(value);
            break;
        case "uint32be":
            buf.writeUInt32BE(value);
            break;
        case "floatle":
            buf.writeFloatLE(value);
            break;
        case "floatbe":
            buf.writeFloatBE(value);
            break;
        case "bool32":
            buf.writeUInt32BE(value);
            break;
        default:
            throw new Error("Unsupported 32-bit type: " + type);
    }

    const useLE = type.endsWith("le");
    const reg0 = useLE ? buf.readUInt16LE(0) : buf.readUInt16BE(0);
    const reg1 = useLE ? buf.readUInt16LE(2) : buf.readUInt16BE(2);

    return [reg0, reg1];
}

if (matchingProtocol && matchingCommand && matchingProtocol.scale <= 1) {
    if (typeof command.value === "string") {
        const trimmed = command.value.trim();
        if (trimmed !== "" && !isNaN(trimmed)) {
            command.value = Number(trimmed);
        }
    }
    const registerLength = global.get("function.typeToRegisterLength")(matchingProtocol.type);
    response.value = {
        actual: flow.get("bridge.image.metrics")[matchingProtocol.id],
        target: command.value
    };
    msg.protocolCommand = matchingCommand;
    msg.response = response;

    if (matchingProtocol.type.includes("bool") && typeof command.value === "boolean") {
        if (matchingProtocol.invert) {
            command.value = !command.value;
        }

        if (matchingCommand.fc === 6 || matchingCommand.fc === 16) {
            const protocolBits = flow.get("bridge.image.protocols").filter(protocol => protocol.address === matchingProtocol.address);
            let registerValue = 0;
            protocolBits.forEach(protocolBit => {
                if (flow.get("bridge.image.metrics")[protocolBit.id] === true) {
                    registerValue += Math.pow(2, protocolBit.offsetbit);
                }
            });
            if (command.value === true) {
                registerValue += Math.pow(2, matchingProtocol.offsetbit);
            }
            command.value = registerValue;
        }

        const valueToSend = ensureArrayForMultipleFC(command.value, matchingCommand.fc);

        msg.payload = {
            value: valueToSend,
            fc: matchingCommand.fc,
            unitid: flow.get("bridge.image.controller.unitId"),
            address: matchingProtocol.address,
            quantity: registerLength
        };

    } else if (matchingProtocol.type.includes("bool") && typeof command.value === "number") {
        if (matchingProtocol.invert) {
            command.value = Math.abs(command.value - 1);
        }

        if (matchingCommand.fc === 6 || matchingCommand.fc === 16) {
            const protocolBits = flow.get("bridge.image.protocols").filter(protocol => protocol.address === matchingProtocol.address);
            let registerValue = 0;
            protocolBits.forEach(protocolBit => {
                if (flow.get("bridge.image.metrics")[protocolBit.id] === true) {
                    registerValue += Math.pow(2, protocolBit.offsetbit);
                }
            });
            if (command.value === 1) {
                registerValue += Math.pow(2, matchingProtocol.offsetbit);
            }
            command.value = registerValue;
        }

        const valueToSend = ensureArrayForMultipleFC(command.value, matchingCommand.fc);

        msg.payload = {
            value: valueToSend,
            fc: matchingCommand.fc,
            unitid: flow.get("bridge.image.controller.unitId"),
            address: matchingProtocol.address,
            quantity: registerLength
        };

    } else if (typeof command.value === "number") {
        // scale first (logical -> raw)
        let scaledValue = command.value * (1 / (matchingProtocol.scale || 1));
        let valueToSend;

        if (registerLength === 2) {
            // 32-Bit Wert in zwei Register nach Typ
            valueToSend = encode32ToRegisters(scaledValue, matchingProtocol.type);
        } else {
            // 1 Register: 8-/16-Bit Typen
            const regValue = encode16ToRegister(scaledValue, matchingProtocol.type);
            valueToSend = regValue;
        }

        valueToSend = ensureArrayForMultipleFC(valueToSend, matchingCommand.fc);

        msg.payload = {
            value: valueToSend,
            fc: matchingCommand.fc,
            unitid: flow.get("bridge.image.controller.unitId"),
            address: matchingProtocol.address,
            quantity: registerLength
        };
    } else {
        const valueToSend = ensureArrayForMultipleFC(command.value, matchingCommand.fc);

        msg.payload = {
            value: valueToSend,
            fc: matchingCommand.fc,
            unitid: flow.get("bridge.image.controller.unitId"),
            address: matchingProtocol.address,
            quantity: registerLength
        };
    }

    let responseMessage = { payload: { response: response } };
    responseMessage.payload.topic = flow.get("bridge").uplink.endpoint.url + "/command/response";
    responseMessage.payload.response.status = "ERROR";
    responseMessage.payload.response.error = "ERROR_BRIDGE_COMMAND_EXECUTION";

    return [msg, responseMessage];

} else {
    node.warn("Command configuration wrong!");
    let responseMessage = { payload: { response: response } };
    responseMessage.payload.topic = flow.get("bridge").uplink.endpoint.url + "/command/response";
    responseMessage.payload.response.status = "ERROR";
    responseMessage.payload.response.error = "ERROR_BRIDGE_COMMAND_CONFIG";

    return [null, responseMessage];
}

let writeMessage = { payload:  msg.payload };
writeMessage.protocolCommand = msg.protocolCommand;

let responseMessage = { payload: { response: msg.response } };
responseMessage.payload.topic = flow.get("bridge").uplink.endpoint.url + "/command/response";
responseMessage.payload.response.status = "OK";
responseMessage.payload.response.error = "";

return [writeMessage, responseMessage];

flow.get("bridge.image.protocols").filter(protocol => protocol.type === "virtual").forEach(dataPoint => {
    if(dataPoint.id === "COMMUNICATION_ERROR") {
        msg.dataPoint = dataPoint;
        msg.name = dataPoint.id;
        msg.payload = false;
        node.send(msg);
    }
});

node.done();

flow.set("bridge.image.metrics", {});

flow.get("bridge.image.protocols").filter(protocol => protocol.type === "virtual").forEach(dataPoint => {
    if(dataPoint.id === "COMMUNICATION_ERROR") {
        msg.dataPoint = dataPoint;
        msg.name = dataPoint.id;
        msg.payload = true;
        node.send(msg);
    }
});

node.done();

flow.get("bridge.image.protocols").filter(protocol => protocol.type === "virtual").forEach(dataPoint => {
    if(dataPoint.id === "COMMUNICATION_ERROR") {
        msg.dataPoint = dataPoint;
        msg.name = dataPoint.id;
        msg.payload = false;
        node.send(msg);
    }
});

node.done();

var updatedPayload = {};
updatedPayload[msg.name] = msg.payload;
msg.payload = updatedPayload;

return msg;

