node.done();

let writeMessage = { payload:  msg.payload };
writeMessage.protocolCommand = msg.protocolCommand;
writeMessage.registerType = msg.payload.fc;

return writeMessage;

if (flow.get("bridge.modbusGateway") && flow.get("bridge.modbusGateway.enabled") === true) {
    flow.get("bridge.modbusGateway.registerMappings").forEach(mappingRegister => {
        if (flow.get("bridge.image.metrics")[mappingRegister.protocolId]) {
            const msgClone = RED.util.cloneMessage(msg)
            msgClone.payload.command = { "value": flow.get("bridge.image.metrics")[mappingRegister.protocolId] };
            msgClone.payload.mappingCommand = {
                "protocolId": mappingRegister.protocolId,
                "type": mappingRegister.dataType,
                "fc": mappingRegister.fc,
            }
            node.send(msgClone);
        }
    });
