var bridge = global.get("cloud.bridges").find(bridge => {
    return bridge.id === msg.payload.find(flow => flow.id === msg.thisflow).label;
})
bridge.image.metrics = {};

flow.set("bridge", bridge);

return msg;

if (msg.payload.protocol.type.includes("bool") && typeof msg.payload.value === "boolean") {
    if (msg.payload.protocol.invert) {
        msg.payload.value = !msg.payload.value;
    }
    msg.payload = {
        "value": msg.payload.value,
        "fc": 5,
        "unitid": flow.get("bridge.image.controller.unitId"),
        "address": msg.payload.protocol.address,
        "quantity": 1
    }
} else if (msg.payload.protocol.type.includes("bool") && typeof msg.payload.value !== "boolean") {
    if (msg.payload.protocol.invert) {
        msg.payload.value = msg.payload.value * -1;
    }
    msg.payload = {
        "value": msg.payload.value,
        "fc": 6,
        "unitid": flow.get("bridge.image.controller.unitId"),
        "address": msg.payload.protocol.address,
        "quantity": 1
    }
} else {
    msg.payload = {
        "value": parseInt(msg.payload.value) * (1 / msg.payload.protocol.scale),
        "fc": 16,
        "unitid": flow.get("bridge.image.controller.unitId"),
        "address": msg.payload.protocol.address,
        "quantity": global.get("function.typeToRegisterLength")(msg.payload.protocol.type)
    }
}

node.send(msg);

const controllerParameter = flow.get("bridge.image.controller");
const protocols = flow.get("bridge.image.protocols");
var channel = global.get("hardware.interface.serial.channels").concat(global.get("hardware.interface.tcp.channels")).find(channel => channel.id === controllerParameter.channel);

if (channel && controllerParameter && protocols) {
    if (channel.connectorType == "TCP") {
        msg.payload = {
            "connectorType": controllerParameter.connectorType ? controllerParameter.connectorType : channel.connectorType,
            "tcpHost": controllerParameter.tcpHost,
            "tcpPort": controllerParameter.tcpPort,
            "tcpType": controllerParameter.tcpType ? controllerParameter.tcpType : "DEFAULT",
            "unitId": controllerParameter.unitId ? controllerParameter.unitId : 1,
            "commandDelay": controllerParameter.commandDelay ? controllerParameter.commandDelay : 5,
            "clientTimeout": controllerParameter.clientTimeout ? controllerParameter.clientTimeout : 1000,
            "reconnectTimeout": controllerParameter.reconnectTimeout ? controllerParameter.reconnectTimeout : 2000
        };
    } else {
        msg.payload = {
            "connectorType": controllerParameter.connectorType ? controllerParameter.connectorType : channel.connectorType,
            "serialPort": controllerParameter.serialPort ? controllerParameter.serialPort : channel.serialPort,
            "serialBaudrate": controllerParameter.serialBaudrate ? controllerParameter.serialBaudrate : 19200,
            "serialDatabits": controllerParameter.serialDatabits ? controllerParameter.serialDatabits : 8,
            "serialStopbits": controllerParameter.serialStopbits ? controllerParameter.serialStopbits : 1,
            "serialParity": controllerParameter.serialParity ? controllerParameter.serialParity : "none",
            "serialType": controllerParameter.serialType ? controllerParameter.serialType : "RTU-BUFFERD",
            "serialConnectionDelay": controllerParameter.serialConnectionDelay ? controllerParameter.serialConnectionDelay : 100,
            "unitId": controllerParameter.unitId ? controllerParameter.unitId : 1,
            "commandDelay": controllerParameter.commandDelay ? controllerParameter.commandDelay : 5,
            "clientTimeout": controllerParameter.clientTimeout ? controllerParameter.clientTimeout : 1000,
            "reconnectTimeout": controllerParameter.reconnectTimeout ? controllerParameter.reconnectTimeout : 2000
        };
    }
    node.send(msg);
} else {
    node.warn("Invalid channel configuration!");
}

node.done()

var cloudBridge = global.get("cloud.bridges").find(bridge => {
    return bridge.id === flow.get("bridge").id
})

for (var propertyName in msg.payload) {
    if (typeof msg.payload[propertyName] === "number") {
        // Round to 2 decimal
        msg.payload[propertyName] = Math.round((msg.payload[propertyName] + Number.EPSILON) * 100) / 100
    }
    flow.get("bridge").image.metrics[propertyName] = msg.payload[propertyName];
}

cloudBridge.image.metrics = flow.get("bridge").image.metrics;

return msg;

msg.payload = {
    "payload" : msg.payload,
    "error" : msg.error
};

if (msg._error){
    msg.payload._error = msg._error;
}

return msg;

var bridge = global.get("cloud.bridges").find(bridge => bridge.id === flow.get("bridge.id"));
if (bridge) {
    msg.topic = bridge.uplink.endpoint.url + "/command/bridge/update";
    msg.payload = { response: {} };
    msg.payload.response = bridge.updateRequest.response;
    msg.payload.response.status = "ERROR";
    msg.payload.response.error = "Bridge publish failed";

    return msg;
}

node.done();
