        node.send({ "payload": { value: data.map(x => { return Math.round(x * 100) }), 'fc': 16, 'unitid': 1, 'address': registerStart, 'quantity': data.length } });
    } else if (metricId === "FORECAST_HUMIDITY") {
        const registerStart = 12200;
