// @ts-ignore
const jwt = global.get('jsonwebtoken');
const fs = global.get('fs');
const crypto = global.get('crypto');

const clientId = env.get('AOP_API_CLIENT_ID');
const scope = env.get('AOP_API_SCOPE');
const tenantId = env.get('AOP_TENANT_ID');
const tokenEndpoint = `https://login.microsoftonline.com/${tenantId}/oauth2/v2.0/token`;
const privateKey = fs.readFileSync("/config/aopPrivateKey.pem");
const cert = fs.readFileSync("/config/aopCertificate.pem");
const x509 = new crypto.X509Certificate(cert);
const sha1 = crypto.createHash("sha1").update(x509.raw).digest("hex").toUpperCase();
const base64urlThumbprint = Buffer.from(sha1, "hex")
  .toString("base64")
  .replace(/\+/g, "-")
  .replace(/\//g, "_")
  .replace(/=+$/, "");

const now = Math.floor(Date.now() / 1000);
// build JWT payload (valid for 10 min is allowed — 1 min is too short)
const payload = {
  aud: tokenEndpoint, // must match your /oauth2/v2.0/token URL exactly
  iss: clientId,
  sub: clientId,
  jti: crypto.randomUUID(),
  nbf: now,
  exp: now + 600 // 10 minutes is OK
};

// JWT header — use x5t (base64url thumbprint)
const header = {
  alg: "RS256",
  typ: "JWT",
  x5t: base64urlThumbprint
}

// sign JWT
const token = jwt.sign(payload, privateKey, {
  algorithm: "RS256",
  header
});

// use URLSearchParams for correct form encoding
const params = new URLSearchParams({
  grant_type: "client_credentials",
  client_id: clientId,
  scope: scope,
  client_assertion_type: "urn:ietf:params:oauth:client-assertion-type:jwt-bearer",
  client_assertion: token
});

msg.payload = params.toString();
msg.url = tokenEndpoint;
msg.method = "POST";

return msg;

